<?phpnamespace Icube\Ordernotifications\Block;use Magento\Framework\View\Element\Template;use Icube\Ordernotifications\Model\OrdernotificationsFactory;class Index extends Template{		protected $_ordernotificationsFactory;	protected $request;    	public function __construct(      Template\Context $context,	  \Magento\Framework\App\Request\Http $request,      OrdernotificationsFactory $ordernotificationsFactory,      array $data = []   ) {        $this->_ordernotificationsFactory = $ordernotificationsFactory;		$this->request = $request;        parent::__construct($context, $data);   }      protected  function _construct()    {        parent::_construct();		$om = \Magento\Framework\App\ObjectManager::getInstance();		$customerSession = $om->get('Magento\Customer\Model\Session');		if($customerSession->isLoggedIn()) {			$collection = $this->_ordernotificationsFactory->create()->getCollection()				->addFieldToFilter('ordernotifications_id',  $this->request->getParam('id'));							$this->setCollection($collection);		}    }		protected function _prepareLayout()    {        parent::_prepareLayout();               $this->getCollection()->load();         return $this;    }       }