<?php namespace Icube\Ordernotifications\Controller\Index;use Magento\Framework\App\Action\Action;use Magento\Framework\App\Action\Context;use Magento\Framework\Controller\Result\JsonFactory;use Magento\Framework\View\Result\PageFactory; class Index extends Action{	private $jsonFactory;    private $pageFactory;    public function __construct(        Context $context,        JsonFactory $jsonFactory,        PageFactory $pageFactory    ){        $this->jsonFactory = $jsonFactory;        $this->pageFactory = $pageFactory;        parent::__construct($context);    }	    public function execute()    {        $pageFactory = $this->pageFactory->create();                 // Add breadcrumb        /** @var \Magento\Theme\Block\Html\Breadcrumbs */        $breadcrumbs = $pageFactory->getLayout()->getBlock('breadcrumbs');        $breadcrumbs->addCrumb('home',            [                'label' => __('Home'),                'title' => __('Home'),                'link' => $this->_url->getUrl('')            ]        );        $breadcrumbs->addCrumb('ordernotifications',            [                'label' => __('Ordernotifications'),                'title' => __('Ordernotifications')            ]        );         return $pageFactory;    }}